import React, { Component } from 'react';

class SortingVisualizer extends Component {
  render() {
    const { array } = this.props;

    return (
      <div className="array-container">
        {array.map((value, idx) => (
          <div
            className="array-bar"
            key={idx}
            style={{
              height: `${value.val}px`,
              backgroundColor: value.backgroundColor || 'blue', // Set default background color
            }}
          ></div>
        ))}
      </div>
    );
  }
}

export default SortingVisualizer;
