import React, { Component } from 'react';
import SortingVisualizer from './SortingVisualizer';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      array: [], // The array of bars to be sorted
      sortingAlgorithm: 'Bubble Sort', // Default sorting algorithm
      sortingSpeed: 50, // Default sorting speed (in milliseconds)
      numberOfBars: 50, // Default number of bars
    };
  }

  // Method to generate a new random array of bars
  generateRandomArray = () => {
    const newArray = [];
    const { numberOfBars } = this.state;
    for (let i = 0; i < numberOfBars; i++) {
      newArray.push(Math.floor(Math.random() * 300) + 1); // Random values between 1 and 100
    }
    this.setState({ array: newArray });
  };

    // Handler for the slider input
  handleSliderChange = (event) => {
    // Update the numberOfBars in state when the slider value changes
    this.setState({ numberOfBars: parseInt(event.target.value) });
  };

  render() {
    const { array, numberOfBars } = this.state;

    return (
      <div>
        <div>
          {/* Slider for number of bars */}
          <label>Number of Bars: {numberOfBars}</label>
          <input
            type="range"
            min="10" // Adjust the minimum and maximum values as needed
            max="500"
            value={numberOfBars}
            onChange={this.handleSliderChange}
          />
        </div>
        <button onClick={this.generateRandomArray}>New Array</button>
        <SortingVisualizer array={array} />
        {/* Other UI components */}
      </div>
    );
  }
}

export default App;
