import React, { Component } from 'react';

class SortingVisualizer extends Component {
  render() {
    const { array } = this.props;

    return (
      <div className="array-container">
        {array.map((bar, idx) => (
          <div
            className="array-bar"
            key={idx}
            style={{
              height: `${bar.value}px`,
              backgroundColor: bar.highlighted ? 'red' : 'blue', /*highlighted changes color of chosen bar during sorting*/
            }}
          >
            {bar.value}
          </div>
        ))}
      </div>
    );
  }
}

export default SortingVisualizer;
